import {React, Component} from 'react';
import { BreakLine } from './LineBreak'
import Form from 'react-bootstrap/Form'
import firebase from './../config.js' 
import config from "../config";
import Col from 'react-bootstrap/Col'
import styled from 'styled-components';

const Style = styled.div`
.signup {
	color: #665544;
	padding-bottom: 70px;
	padding-left: 30%;
	text-align: left;

}
.form-group {
	width:60%;
}

.button {
	bacground-color: #665544;
}
`
const StyleButton = styled.button`
	color: white;
	background-color: #665544;
	padding: 8px;
	&:hover { background-color: #d4d0c6; color:#665544 }`

class SubmitForm extends Component {

	constructor(props) {
    super(props);
    this.state = {
      form: [],
      alert: false,
      alertData: {}
    };
  }

  showAlert(type, message) {
    this.setState({
      alert: true,
      alertData: { type, message }
    });
    setTimeout(() => {
      this.setState({ alert: false });
    }, 4000)
  }

  resetForm() {
    this.refs.contactForm.reset();
  }

  componentWillMount() {
    let formRef = firebase.database().ref('form').orderByKey().limitToLast(6);
    formRef.on('child_added', snapshot => {
      const { name, email, city, phone, message } = snapshot.val();
      const data = { name, email, city, phone, message };
      this.setState({ form: [data].concat(this.state.form) });
    })
  }

  handleSubmit(e) {
    e.preventDefault();
    const params = {
      name: this.inputName.value,
      email: this.inputEmail.value,
      city: this.inputCity.value,
      phone: this.inputPhone.value,
      message: this.textAreaMessage.value
    };
    if (params.name && params.email && params.phone && params.phone && params.message) {
      firebase.database().ref('form').push(params).then(() => {
        this.showAlert('success', 'Your message was sent successfull');
      }).catch(() => {
        this.showAlert('danger', 'Your message could not be sent');
      });
      this.resetForm();
    } else {
      this.showAlert('warning', 'Please fill the form');
    };
  }

	render() {
		return (
			<Style>
				<div className="title">
					<h1>Let's get painting!</h1>
					<p>Sign up for our workshop</p>
					<BreakLine w="50%" />
				</div>
        {this.state.alert && <div className={`alert alert-${this.state.alertData.type}`} role='alert'>
          <div className='container'>
            {this.state.alertData.message}
          </div>
        </div>}
				<div className="signup">
					<Form noValidate validated={this.validated} onSubmit={this.handleSubmit.bind(this)}>
						<Form.Group as={this.Row} controlId="formName">
							<Form.Label column sm={2}>
								Your Name
									</Form.Label>
							<Col sm={10}>
								<Form.Control
									required
									type="text"
									placeholder="Name"
								/>
							</Col>
						</Form.Group>

						<Form.Group as={this.Row} controlId="formEvent">
							<Form.Label column sm={2}>
								Event
									</Form.Label>
							<Col sm={10}>
								<Form.Control
									required
									type="text"
									placeholder="Name"
								/>
							</Col>
						</Form.Group>
						<fieldset>
							<Form.Group as={this.Row}>
								<Form.Label as="Payment" column sm={2}>
									Payment Type
							</Form.Label>
								<Col sm={10}>
									<Form.Check
										required
										type="radio"
										label="cash"
										name="formHorizontalRadios"
										id="formPayment1"
									/>
									<Form.Check
										type="radio"
										label="credit card"
										name="formHorizontalRadios"
										id="formPayment2"
									/>
								</Col>
							</Form.Group>
						</fieldset>
						<Form.Group as={this.Row} controlId="formCheck">
							<Col sm={{ span: 10, offset: 2 }}>
								<p>By clicking confirmation you agree to our Terms & Condition</p>
								<Form.Check
									required
									name="terms"
									label="Agree to terms and conditions"
								/>
							</Col>
						</Form.Group>

						<Form.Group as={this.Row}>
							<Col sm={{ span: 10, offset: 2 }}>
								<StyleButton type="submit">Confirm</StyleButton>
							</Col>
						</Form.Group>
					</Form>
				</div>
			</Style>
		);
	}
}
export default SubmitForm;
