import React from 'react';
import Container from '@material-ui/core/Container';
import CssBaseline from '@material-ui/core/CssBaseline';
import Button from '@material-ui/core/Button';
import tileData from './otherImages';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
	container: {
		padding: 30,
		border: '1px solid #665544',
		height: '30%',
		// textAlign: 'left',
		marginTop: 50,
		display: 'flex',
		flexDirection: 'row',
	},
	description: {
		width: '70%',
		color: '#665544',
		padding: 70,
		// alignContent: 'left',
		// fontStyle: 'italic',
		// fontFamily: 'courier',
	},
	// @media (min-width: 900px) {
	// 	width: 800px;
	// },
}));

export default function SimpleContainer(props) {
	const classes = useStyles();
	return (
		<React.Fragment>
			<CssBaseline />
			<Container className={classes.container} maxWidth="md">
				<img
					src={tileData[12].img}
					alt={tileData[12].title}
					width={200}
					height={250} />

				<div className={classes.description}>
					<h3>Who are we?</h3>
					<hr color='#665544'></hr>
					<p>CungDesign captures the beautiful moment of nature, human and life. We bring creativity to inspire the art in each person.</p>
					<Button size="small" variant="outlined" color='#665544' onClick={() => {
						props.switchPage(4);
						window.scrollTo(0, 0);
					}}>
						Learn More
          </Button>
				</div>
			</Container>

			<Container className={classes.container} maxWidth="md">
				<div className={classes.description}>
					<h3>Painting of the Week</h3>
					<hr color='#665544'></hr>
					<p>"Fall in Love" is the theme painting of this week. Painted with Acrylic paints and captured while wandring around to see these beautiful pair of swans  </p>
					<Button size="small" variant="outlined" color='#665544' onClick={() => {
						props.switchPage(2);
						window.scrollTo({
							top: 200,
							behavior: 'smooth'
						  });
					}}>
						See Other Paintings
          </Button>
				</div>
				<img
					src={tileData[8].img}
					alt={tileData[8].title}
					width={200}
					height={250} />
			</Container>
		</React.Fragment>
	);
}
