import React from 'react';
import { BreakLine } from './LineBreak'
import Form from 'react-bootstrap/Form'
import Firebase from "firebase";
import config from "./config";
import Col from 'react-bootstrap/Col'
import styled from 'styled-components';

const Style = styled.div`
.signup {
	color: #665544;
	padding-bottom: 70px;
	padding-left: 30%;
	text-align: left;

}
.form-group {
	width:60%;
}

.button {
	bacground-color: #665544;
}
`
const StyleButton = styled.button`
	color: white;
	background-color: #665544;
	padding: 8px;
	&:hover { background-color: #d4d0c6; color:#665544 }`

	class App extends React.Component {
		constructor(props) {
			super(props);
			Firebase.initializeApp(config);

			this.state = {
				developers: []
			};
		}

		componentDidMount() {
			this.getUserData();
		}

		componentDidUpdate(prevProps, prevState) {
			if (prevState !== this.state) {
				this.writeUserData();
			}
		}

		writeUserData = () => {
			Firebase.database()
				.ref("/")
				.set(this.state);
			console.log("DATA SAVED");
		};

		getUserData = () => {
			let ref = Firebase.database().ref("/");
			ref.on("value", snapshot => {
				const state = snapshot.val();
				this.setState(state);
			});
		};

		handleSubmit = event => {
			event.preventDefault();
			let name = this.refs.name.value;
			let role = this.refs.role.value;
			let uid = this.refs.uid.value;

			if (uid && name && role) {
				const { developers } = this.state;
				const devIndex = developers.findIndex(data => {
					return data.uid === uid;
				});
				developers[devIndex].name = name;
				developers[devIndex].role = role;
				this.setState({ developers });
			} else if (name && role) {
				const uid = new Date().getTime().toString();
				const { developers } = this.state;
				developers.push({ uid, name, role });
				this.setState({ developers });
			}

			this.refs.name.value = "";
			this.refs.role.value = "";
			this.refs.uid.value = "";
		};

		removeData = developer => {
			const { developers } = this.state;
			const newState = developers.filter(data => {
				return data.uid !== developer.uid;
			});
			this.setState({ developers: newState });
		};

		updateData = developer => {
			this.refs.uid.value = developer.uid;
			this.refs.name.value = developer.name;
			this.refs.role.value = developer.role;
		};

	render() {
    const { developers } = this.state;
    return (
		<Style>
			<div className="title">
				<h1>Let's get painting!</h1>
				<p>Sign up for our workshop</p>
				<BreakLine w="50%" />
			</div>
			<div className="signup">
				<Form noValidate validated={validated} onSubmit={handleSubmit}>
					<Form.Group as={Row} controlId="formHorizontalEmail">
						<Form.Label column sm={2}>
							Your Name
									</Form.Label>
						<Col sm={10}>
						<Form.Control
							required
							type="text"
							placeholder="Name"
						/>
						</Col>
					</Form.Group>

					<Form.Group as={Row} controlId="formHorizontalPassword">
						<Form.Label column sm={2}>
							Event
									</Form.Label>
						<Col sm={10}>
						<Form.Control
							required
							type="text"
							placeholder="Name"
						/>
						</Col>
					</Form.Group>
					<fieldset>
						<Form.Group as={Row}>
							<Form.Label as="legend" column sm={2}>
								Payment Type
							</Form.Label>
							<Col sm={10}>
								<Form.Check
									required
									type="radio"
									label="cash"
									name="formHorizontalRadios"
									id="formHorizontalRadios1"
								/>
								<Form.Check
									type="radio"
									label="credit card"
									name="formHorizontalRadios"
									id="formHorizontalRadios2"
								/>
							</Col>
						</Form.Group>
					</fieldset>
					<Form.Group as={Row} controlId="formHorizontalCheck">
						<Col sm={{ span: 10, offset: 2 }}>
							<p>By clicking confirmation you agree to our Terms & Condition</p>
							<Form.Check
								required
								name="terms"
								label="Agree to terms and conditions"
							/>
						</Col>
					</Form.Group>

					<Form.Group as={Row}>
						<Col sm={{ span: 10, offset: 2 }}>
							<StyleButton type="submit">Confirm</StyleButton>
						</Col>
					</Form.Group>
				</Form>
			</div>
		</Style>
 );
}
}
