import {React, Component} from 'react';
import { BreakLine } from './LineBreak'
import Form from 'react-bootstrap/Form'
import firebase from './../config' 
import config from "../config";
import Col from 'react-bootstrap/Col'
import styled from 'styled-components';

const Style = styled.div`
.signup {
	color: #665544;
	padding-bottom: 70px;
	padding-left: 30%;
	text-align: left;

}
.form-group {
	width:60%;
}

.button {
	bacground-color: #665544;
}
`
const StyleButton = styled.button`
	color: white;
	background-color: #665544;
	padding: 8px;
	&:hover { background-color: #d4d0c6; color:#665544 }`

class SubmitForm extends Component {

	constructor() {
    super();
    this.state = {
      name: '',
			event: '',
			payment:'',
      terms: '',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  handleSubmit(e) {
    e.preventDefault();
    const itemsRef = firebase.database().ref('items');
    const item = {
      username: this.state.name,
			thisevent: this.state.event,
			thispayment: this.payment,
			thisterms: this.terms
    }
    itemsRef.push(item);
    this.setState({
      name: '',
			event: '',
			payment:'',
      terms: '',
    });
  }
  componentDidMount() {
    const itemsRef = firebase.database().ref('items');
    itemsRef.on('value', (snapshot) => {
      let items = snapshot.val();
      let newState = [];
      for (let item in items) {
        newState.push({
          id: item,
          username: items[item].username,
					thisevent: items[item].thisevent,
					thispayment: items[item].thispayment,
					thisterms: items[item].thisterms,

        });
      }
      this.setState({
        items: newState
      });
    });
  }
  removeItem(itemId) {
    const itemRef = firebase.database().ref(`/items/${itemId}`);
    itemRef.remove();
  }
	render() {
		return (
			<Style>
				<div className="title">
					<h1>Let's get painting!</h1>
					<p>Sign up for our workshop</p>
					<BreakLine w="50%" />
				</div>
        {/* {this.state.alert && <div className={`alert alert-${this.state.alertData.type}`} role='alert'>
          <div className='container'>
            {this.state.alertData.message}
          </div>
        </div>} */}
				<div className="signup">
					<Form noValidate validated={this.validated} onSubmit={this.handleSubmit}>
						<Form.Group as={this.Row} controlId="formName">
							<Form.Label column sm={2}>
								Your Name
									</Form.Label>
							<Col sm={10}>
								<Form.Control
									required
									type="text"
									placeholder="Name"
								/>
							</Col>
						</Form.Group>

						<Form.Group as={this.Row} controlId="formEvent">
							<Form.Label column sm={2}>
								Event
									</Form.Label>
							<Col sm={10}>
								<Form.Control
									required
									type="text"
									placeholder="Name"
								/>
							</Col>
						</Form.Group>
						<fieldset>
							<Form.Group as={this.Row}>
								<Form.Label as="Payment" column sm={2}>
									Payment Type
							</Form.Label>
								<Col sm={10}>
									<Form.Check
										required
										type="radio"
										label="cash"
										name="formHorizontalRadios"
										id="formPayment1"
									/>
									<Form.Check
										type="radio"
										label="credit card"
										name="formHorizontalRadios"
										id="formPayment2"
									/>
								</Col>
							</Form.Group>
						</fieldset>
						<Form.Group as={this.Row} controlId="formCheck">
							<Col sm={{ span: 10, offset: 2 }}>
								<p>By clicking confirmation you agree to our Terms & Condition</p>
								<Form.Check
									required
									name="terms"
									label="Agree to terms and conditions"
								/>
							</Col>
						</Form.Group>

						<Form.Group as={this.Row}>
							<Col sm={{ span: 10, offset: 2 }}>
								<StyleButton type="submit">Confirm</StyleButton>
							</Col>
						</Form.Group>
					</Form>
				</div>
			</Style>
		);
	}
}
export default SubmitForm;
