import React from 'react';
import Figure from 'react-bootstrap/Figure'
import FigureImage from 'react-bootstrap/FigureImage'
import styled from 'styled-components';
import { Container } from 'react-bootstrap';
import {Link} from 'react-router-dom';
import ScrollToTop from './ScrollToTop';
import  Gallery from './Gallery';
import PropTypes from 'prop-types';
import Typography from '@material-ui/core/Typography';

// import Button from 'react-bootstrap/Button'

const Styles = styled.div`
    .container {
        padding: 30px;
        // background-color: rgba(102, 85, 68, 0.7);
        border: 1px solid #665544;
        // width: 70%;
        // min-width = 400px;
        height: 30%;
        text-align: center;
        margin-top: 50px;
        display: flex;
        flex-direction: row;

        @media(mid-width : 900px) {
            width: 800px;
    }
    .figure {
        padding-top: 50px;
    }
    .description {
        width: 50%;
        color: #665544;
        padding: 70px;
        align-content: center;
        font-style: italic;
        font-family: courier;
    }

`
const StyleButton = styled.button`
    background-color: white;
    color: #665544;
    border: 1px solid #665544;
    padding: 8px;
    &:hover { background-color: #d4d0c6; }`;

function TabPanel(props) {
    const { children } = props;

    return (

              <Typography>{children}</Typography>

      );
}
TabPanel.propTypes = {
    children: PropTypes.node,
  };
  const [value, setValue] = React.useState(0);
  const handleClick = (event, newValue) => {
    setValue(newValue);
  };

export const Feature = () => (
    <Styles>
        <Container>
            <div className='description'>
                <h1>Who are we?</h1>
                <hr color = '#665544'></hr>
                <p>" Thdkshf thhgmbjb skhdla  askjhf sakj fal sakf ls khf ;s akuhf lskd hf dshu shxz fzh lfjh sldkhc shc ds csh djchs jhc dsh h chdshjh dhc jsdhc dhjh" </p>
                <ScrollToTop/><Link to="/About"><StyleButton> Read More</StyleButton></Link>
            </div>
            <Figure>
                <FigureImage
                    width={400}
                    height={450}
                    alt="300*450"
                    src="images/bro2.jpg"
                />
            </Figure>
        </Container>
        <Container>
            <Figure>
                <FigureImage
                    width={450}
                    height={300}
                    alt="450*300"
                    src="images/thucan.jpg"
                />
            </Figure>
            <div className='description'>
                <h1>Painting of the Week</h1>
                <hr color = '#665544'></hr>
                <p>" Thdkshf thhgmbjb skhdla  askjhf sakj fal sakf ls khf ;s akuhf lskd hf dshu shxz fzh lfjh sldkhc shc ds csh djchs jhc dsh h chdshjh dhc jsdhc dhjh" </p>
                <button className = "button" onClick= {handleClick}> See more</button>
                <TabPanel>
                <Gallery></Gallery>
                </TabPanel>
            </div>
        </Container>
    </Styles>
)
